import React, { useState } from 'react';
import TodoList from './components/TodoList/index';

function TodoFeature() {
  const generateListTask = [
    {
      id: 1,
      name: 'eat',
      status: 'new',

    },
    {
      id: 2,
      name: 'cook',
      status: 'completed',

    },
    {

      id: 3,
      name: 'code',
      status: 'new',

    }
  ]

  const [listTask, setListTask] = useState(generateListTask);

  const changeStatusTask = (task, idx) => {
    // clone array
    const newTodoList = [...listTask];

    // toggle state
    const taskChange = {
      ...newTodoList[idx],
      status: newTodoList[idx].status === 'new' ? 'completed' : 'new',
    }

    newTodoList[idx] = taskChange;

    // update state
    setListTask(newTodoList);
  }

  return (
    <div>
      <TodoList listTask={listTask} changeStatusTask={changeStatusTask}></TodoList>
    </div>
  )
};

export default TodoFeature;
