import React, { PureComponent } from 'react';

class ColorBox extends PureComponent {

  constructor(props) {
    super(props);

    this.state = {
      sharp: 'rect',
      color: 'blue',
    }

  }

  hander = () => {
    this.setState({color: 'green'})
  }

  render() {
    const { sharp, color } = this.state;
    return (
      <div>
        {color}
        <button onClick={this.hander}>Click Me</button>
      </div>
    )
  }

}

export default ColorBox;
